import React, { Component } from "react";
class LifeCycle extends Component{
    constructor(props){
        super(props);
        this.state={color:"Black"};
    }
    componentDidMount(){
        setTimeout(()=>{
            this.state({color:Blue})},5000
        )
    }
    render(){
        return(
            <h1>My fav color is {this.state.color}</h1>
        );
    }
} 
export default LifeCycle;
